        return new Source("{% extends \"base.html.twig\" %}

{% block body %}

<div class=\"container\">
    <div class=\"row\">
        <h1 class=\"mt-5 mb-5 text-center\">Update A Contatact</h1>

        {% for message in app.flashes('notice') %}
            <div class=\"alert alert-success\">{{message}}</div>
        {% endfor %}
        <div class=\"col-md-6 offset-md-3\">
            {{form_start(form)}}
                <div class = 'form-group'>
                    {{ form_label(form.name) }}
                    {{ form_widget (form.name, {'attr':{'class':'form-control'}}) }}
                </div>
                <div class = 'form-group'>
                    {{ form_label(form.email_add) }}
                    {{ form_widget (form.email_add, {'attr':{'class':'form-control'}}) }}
                </div>
                <div class = 'form-group'>
                    {{ form_label(form.phone_number) }}
                    {{ form_widget (form.phone_number, {'attr':{'class':'form-control'}}) }}
                </div>
                <div class = 'form-group'>
                    {{ form_label(form.phone_number2) }}
                    {{ form_widget (form.phone_number2, {'attr':{'class':'form-control'}}) }}
                </div>
                <div class = 'form-group'>
                    {{ form_label(form.profile_picture) }}
                    {{ form_widget (form.profile_picture, {'attr':{'class':'form-control'}}) }}
                </div>
                <div class = 'form-group'>
                    {{ form_label(form.description) }}
                    {{ form_widget (form.description, {'attr':{'class':'form-control', 'size':'10'}}) }}
                </div>
                
                <div class = 'form-group'>
                    <button class=\"btn btn-info mt-2 col-12\">Update Contect</button>
                </div>
                {{form(form)}}
            {{form_end(form)}}
        </div>
    </div>
</div>

{% endblock %}", "main/update.html.twig", "C:\\xampp\\htdocs\\contact_list\\templates\\main\\update.html.twig");
